-- Columns, data types, nullability, defaults, generated and identity attributes
SELECT
  c.table_schema AS schema,
  c.table_name,
  c.ordinal_position AS column_position,
  c.column_name,
  c.data_type,
  c.udt_name AS udt,
  c.character_maximum_length AS char_max_len,
  c.numeric_precision,
  c.numeric_scale,
  c.is_nullable,
  c.column_default,
  c.is_identity,
  c.identity_generation,
  c.is_generated,
  pgd.description AS column_comment
FROM information_schema.columns c
JOIN information_schema.tables t
  ON t.table_schema = c.table_schema AND t.table_name = c.table_name
LEFT JOIN pg_catalog.pg_class pc
  ON pc.relname = c.table_name
LEFT JOIN pg_catalog.pg_namespace pn
  ON pn.nspname = c.table_schema AND pn.oid = pc.relnamespace
LEFT JOIN pg_catalog.pg_description pgd
  ON pgd.objoid = pc.oid AND pgd.objsubid = c.ordinal_position
WHERE t.table_type IN ('BASE TABLE','VIEW','FOREIGN TABLE','LOCAL TEMPORARY')
  AND c.table_schema NOT IN ('pg_catalog','information_schema')
  AND c.table_schema NOT LIKE 'pg_toast%'
  AND c.table_schema NOT LIKE 'pg_temp%'
  AND c.table_schema = 'public' -- Limit to public schema only
ORDER BY c.table_schema, c.table_name, c.ordinal_position;

