-- Functions and procedures with signature, volatility, language, return type and security
SELECT
  n.nspname AS schema,
  p.proname AS routine_name,
  CASE p.prokind WHEN 'f' THEN 'function' WHEN 'p' THEN 'procedure' WHEN 'a' THEN 'aggregate' WHEN 'w' THEN 'window' END AS kind,
  pg_catalog.pg_get_function_identity_arguments(p.oid) AS args,
  pg_catalog.pg_get_function_result(p.oid) AS return_type,
  l.lanname AS language,
  p.provolatile AS volatility,
  p.prosecdef AS security_definer,
  p.proleakproof AS leakproof,
  p.proparallel AS parallel,
  obj_description(p.oid, 'pg_proc') AS description,
  pg_catalog.pg_get_functiondef(p.oid) AS definition
FROM pg_proc p
JOIN pg_namespace n ON n.oid = p.pronamespace
JOIN pg_language l ON l.oid = p.prolang
WHERE n.nspname NOT IN ('pg_catalog','information_schema')
  AND n.nspname NOT LIKE 'pg_toast%'
  AND n.nspname NOT LIKE 'pg_temp%'
  AND n.nspname = 'public' -- Limit to public schema only
ORDER BY schema, routine_name, args;

