| public | _drop_all_policies                           | function | p_schema text, p_table text                                                                                                                                                                                                                                                                                                                                   | void                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public._drop_all_policies(p_schema text, p_table text)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE r record;
BEGIN
  FOR r IN SELECT policyname FROM pg_policies WHERE schemaname=p_schema AND tablename=p_table
  LOOP
    EXECUTE format('DROP POLICY %I ON %I.%I', r.policyname, p_schema, p_table);
  END LOOP;
END;$function$
