| public | _drop_policy                                 | function | p_schema text, p_table text, p_policy text                                                                                                                                                                                                                                                                                                                    | void                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public._drop_policy(p_schema text, p_table text, p_policy text)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
BEGIN
  IF EXISTS (
    SELECT 1 FROM pg_policies WHERE schemaname=p_schema AND tablename=p_table AND policyname=p_policy
  ) THEN
    EXECUTE format('DROP POLICY %I ON %I.%I', p_policy, p_schema, p_table);
  END IF;
END;$function$
