| public | authenticate_user                            | function | p_supabase_uuid uuid                                                                                                                                                                                                                                                                                                                                          | TABLE(user_id bigint, role character varying, firstname character varying, surname character varying, email character varying) | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.authenticate_user(p_supabase_uuid uuid)
 RETURNS TABLE(user_id bigint, role character varying, firstname character varying, surname character varying, email character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN QUERY
    SELECT 
        u.user_id,
        u.role,
        u.firstname,
        u.surname,
        u.email
    FROM users u
    JOIN user_mapping um ON u.user_id = um.user_id
    WHERE um.uuid = p_supabase_uuid;
END;
$function$
