                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       |
| public | complete_receiving_report                    | function | p_report_id bigint, p_user_id bigint                                                                                                                                                                                                                                                                                                                          | void                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.complete_receiving_report(p_report_id bigint, p_user_id bigint)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
BEGIN
    PERFORM create_inventory_from_report(p_report_id, p_user_id);
    UPDATE receiving_reports SET status = 'COMPLETED', updated_at = NOW(), updated_by = p_user_id
    WHERE report_id = p_report_id;
END; $function$
