                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  |
| public | compute_days_overdue                         | function | scheduled date, status schedule_status_enum, actual date                                                                                                                                                                                                                                                                                                      | integer                                                                                                                        | plpgsql  | i          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.compute_days_overdue(scheduled date, status schedule_status_enum, actual date)
 RETURNS integer
 LANGUAGE plpgsql
 IMMUTABLE STRICT
AS $function$
BEGIN
    IF status IN ('Pending','Scheduled','Due','Overdue') AND actual IS NULL AND scheduled < CURRENT_DATE THEN
        RETURN (CURRENT_DATE - scheduled);
    END IF;
    RETURN 0;
END; $function$
