                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                |
| public | create_user_with_mapping                     | function | p_supabase_uuid uuid, p_role character varying, p_surname character varying, p_firstname character varying, p_middlename character varying, p_sex character varying, p_birthdate date, p_address text, p_contact_number character varying, p_email character varying, p_username character varying, p_password_hash text                                      | bigint                                                                                                                         | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.create_user_with_mapping(p_supabase_uuid uuid, p_role character varying, p_surname character varying, p_firstname character varying, p_middlename character varying DEFAULT NULL::character varying, p_sex character varying DEFAULT 'Other'::character varying, p_birthdate date DEFAULT CURRENT_DATE, p_address text DEFAULT ''::text, p_contact_number character varying DEFAULT NULL::character varying, p_email character varying DEFAULT NULL::character varying, p_username character varying DEFAULT NULL::character varying, p_password_hash text DEFAULT ''::text)
 RETURNS bigint
 LANGUAGE plpgsql
AS $function$
DECLARE
    new_user_id BIGINT;
BEGIN
    -- Insert user
    INSERT INTO users (role, surname, firstname, middlename, sex, birthdate, address, contact_number, email, username, password_hash)
    VALUES (p_role, p_surname, p_firstname, p_middlename, p_sex, p_birthdate, p_address, p_contact_number, p_email, p_username, p_password_hash)
    RETURNING user_id INTO new_user_id;
    
    -- Create mapping with actual Supabase UUID
    INSERT INTO user_mapping (uuid, user_id)
    VALUES (p_supabase_uuid, new_user_id)
    ON CONFLICT (user_id) DO UPDATE SET uuid = p_supabase_uuid;
    
    RETURN new_user_id;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                