                                                                                                                                                                                                          |
| public | debug_reschedule_checkpoints_by              | function | p_patient_id bigint, p_vaccine_id bigint, p_dose_number integer, p_new_date date                                                                                                                                                                                                                                                                              | TABLE(rule text, passed boolean, detail jsonb)                                                                                 | plpgsql  | v          | true             | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.debug_reschedule_checkpoints_by(p_patient_id bigint, p_vaccine_id bigint, p_dose_number integer, p_new_date date)
 RETURNS TABLE(rule text, passed boolean, detail jsonb)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'pg_temp'
AS $function$
DECLARE
    v_ps_id bigint;
BEGIN
    SELECT patient_schedule_id INTO v_ps_id
    FROM patientschedule
    WHERE patient_id = p_patient_id
      AND vaccine_id = p_vaccine_id
      AND dose_number = p_dose_number
      AND COALESCE(is_deleted, false) = false
    ORDER BY patient_schedule_id
    LIMIT 1;

    IF NOT FOUND THEN
        RAISE EXCEPTION 'patientschedule not found for patient %, vaccine %, dose %', p_patient_id, p_vaccine_id, p_dose_number;
    END IF;

    RETURN QUERY SELECT * FROM public.debug_reschedule_checkpoints(v_ps_id, p_new_date);
END;
$function$
