                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            |
| public | debug_reschedule_trace                       | function | p_patient_schedule_id bigint, p_new_date date, p_user_id bigint                                                                                                                                                                                                                                                                                               | TABLE(trace jsonb, changes jsonb)                                                                                              | plpgsql  | v          | true             | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.debug_reschedule_trace(p_patient_schedule_id bigint, p_new_date date, p_user_id bigint DEFAULT NULL::bigint)
 RETURNS TABLE(trace jsonb, changes jsonb)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'pg_temp'
AS $function$
DECLARE
    v_changes jsonb;
BEGIN
    -- Create temp trace table for this session
    CREATE TEMP TABLE IF NOT EXISTS pg_temp.tmp_reschedule_trace (
        id bigserial primary key,
        phase text,
        candidate_date date,
        rule text,
        decision text,
        context jsonb
    ) ON COMMIT DROP;

    -- Run the recursive rescheduler and collect changed rows
    WITH r AS (
        SELECT * FROM reschedule_patientschedule_recursive(p_patient_schedule_id, p_new_date, p_user_id, 200)
    )
    SELECT COALESCE(json_agg(to_jsonb(r.*) ORDER BY r.patient_schedule_id), '[]'::jsonb)
    INTO v_changes
    FROM r;

    -- Return one row with the trace and changes payloads
    RETURN QUERY
    SELECT 
        COALESCE((SELECT json_agg(to_jsonb(t) ORDER BY t.id) FROM pg_temp.tmp_reschedule_trace t), '[]'::jsonb) AS trace,
        v_changes AS changes;
END;
$function$
