                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              |
| public | ensure_activity_action_type                  | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.ensure_activity_action_type()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
        BEGIN
            IF NEW.action_type IS NOT NULL AND NOT EXISTS (
                SELECT 1 FROM activity_action_types WHERE action_type = NEW.action_type
            ) THEN
                RAISE EXCEPTION 'Unknown action_type %', NEW.action_type;
            END IF;
            RETURN NEW;
        END;$function$
