| public | ensure_patient_qr_nonce                      | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.ensure_patient_qr_nonce()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
begin
  if NEW.qr_nonce is null then
    NEW.qr_nonce := gen_random_uuid();
  end if;
  return NEW;
end;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                