| public | get_user_id_from_uuid                        | function | p_uuid uuid                                                                                                                                                                                                                                                                                                                                                   | bigint                                                                                                                         | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.get_user_id_from_uuid(p_uuid uuid)
 RETURNS bigint
 LANGUAGE plpgsql
AS $function$
DECLARE
    user_id_result BIGINT;
BEGIN
    SELECT user_id INTO user_id_result
    FROM user_mapping
    WHERE uuid = p_uuid;
    
    RETURN user_id_result;
END;
$function$
