| public | get_uuid_from_user_id                        | function | p_user_id bigint                                                                                                                                                                                                                                                                                                                                              | uuid                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.get_uuid_from_user_id(p_user_id bigint)
 RETURNS uuid
 LANGUAGE plpgsql
AS $function$
DECLARE
    uuid_result UUID;
BEGIN
    SELECT uuid INTO uuid_result
    FROM user_mapping
    WHERE user_id = p_user_id;
    
    RETURN uuid_result;
END;
$function$
