                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 |
| public | ingest_patient_immunization_history          | function | p_patient_id bigint, p_history jsonb, p_user_id bigint                                                                                                                                                                                                                                                                                                        | void                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.ingest_patient_immunization_history(p_patient_id bigint, p_history jsonb, p_user_id bigint DEFAULT NULL::bigint)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE
    rec jsonb;
    v_vaccine_id bigint;
    v_dose int;
    v_date date;
    v_remarks text;
    v_actor uuid := current_setting('app.user_id', true)::uuid;
BEGIN
    IF jsonb_typeof(p_history) <> 'array' THEN
        RAISE EXCEPTION 'History must be a JSON array';
    END IF;

    FOR rec IN SELECT * FROM jsonb_array_elements(p_history) LOOP
        v_vaccine_id := (rec->>'vaccine_id')::bigint;
        v_dose := (rec->>'dose_number')::int;
        v_date := (rec->>'administration_date')::date;
        v_remarks := rec->>'remarks';
        IF v_vaccine_id IS NULL OR v_dose IS NULL OR v_date IS NULL THEN
            RAISE EXCEPTION 'Invalid entry (vaccine_id, dose_number, administration_date required) %', rec;
        END IF;

        -- Insert immunization if missing
        INSERT INTO immunizations(visit_id, vaccine_id, dose_number, administered_by, remarks, created_by, updated_by)
        VALUES (NULL, v_vaccine_id, v_dose, p_user_id, v_remarks, p_user_id, p_user_id)
        ON CONFLICT DO NOTHING;

        -- Mark schedule row completed if it exists
        UPDATE patientschedule
           SET actual_date = v_date,
               status = 'Completed',
               updated_at = now(),
               updated_by = p_user_id
         WHERE patient_id = p_patient_id AND vaccine_id = v_vaccine_id AND dose_number = v_dose;

        INSERT INTO activitylogs(action_type, actor_user_id, target_table, target_id, details)
        SELECT 'VACCINE_HISTORY_IMPORTED', v_actor, 'patientschedule', ps.patient_schedule_id,
               jsonb_build_object('historic_date', v_date, 'dose', v_dose, 'vaccine_id', v_vaccine_id)
        FROM patientschedule ps
        WHERE patient_id = p_patient_id AND vaccine_id = v_vaccine_id AND dose_number = v_dose;
    END LOOP;

    -- Recompute statuses post ingestion
    PERFORM recompute_patient_schedule_statuses(p_patient_id);
END; $function$
