| public | insert_patient_bypass_trigger                | function | p_firstname text, p_surname text, p_middlename text, p_date_of_birth date, p_sex text, p_address text, p_barangay text, p_health_center text, p_guardian_id bigint, p_mother_name text, p_mother_occupation text, p_mother_contact_number text, p_father_name text, p_father_occupation text, p_father_contact_number text, p_family_number text, p_tags text | json                                                                                                                           | plpgsql  | v          | true             | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.insert_patient_bypass_trigger(p_firstname text, p_surname text, p_middlename text DEFAULT NULL::text, p_date_of_birth date DEFAULT NULL::date, p_sex text DEFAULT NULL::text, p_address text DEFAULT NULL::text, p_barangay text DEFAULT NULL::text, p_health_center text DEFAULT NULL::text, p_guardian_id bigint DEFAULT NULL::bigint, p_mother_name text DEFAULT NULL::text, p_mother_occupation text DEFAULT NULL::text, p_mother_contact_number text DEFAULT NULL::text, p_father_name text DEFAULT NULL::text, p_father_occupation text DEFAULT NULL::text, p_father_contact_number text DEFAULT NULL::text, p_family_number text DEFAULT NULL::text, p_tags text DEFAULT NULL::text)
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  new_patient_id bigint;
  result_json json;
BEGIN
  INSERT INTO patients (
    firstname, surname, middlename, date_of_birth, sex, address, barangay, health_center, guardian_id,
    mother_name, mother_occupation, mother_contact_number,
    father_name, father_occupation, father_contact_number,
    family_number, tags, date_registered
  ) VALUES (
    p_firstname, p_surname, p_middlename, p_date_of_birth, p_sex,
    p_address, p_barangay, p_health_center, p_guardian_id,
    p_mother_name, p_mother_occupation, p_mother_contact_number,
    p_father_name, p_father_occupation, p_father_contact_number,
    p_family_number, p_tags, CURRENT_TIMESTAMP
  ) RETURNING patient_id INTO new_patient_id;

  result_json := json_build_object(
    'patient_id', new_patient_id,
    'firstname', p_firstname,
    'surname', p_surname,
    'middlename', p_middlename,
    'date_of_birth', p_date_of_birth,
    'sex', p_sex,
    'address', p_address,
    'barangay', p_barangay,
    'health_center', p_health_center,
    'guardian_id', p_guardian_id,
    'mother_name', p_mother_name,
    'mother_occupation', p_mother_occupation,
    'mother_contact_number', p_mother_contact_number,
    'father_name', p_father_name,
    'father_occupation', p_father_occupation,
    'father_contact_number', p_father_contact_number,
    'family_number', p_family_number,
    'tags', p_tags,
    'date_registered', CURRENT_TIMESTAMP
  );

  RETURN result_json;
EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Error creating patient: %', SQLERRM;
END;
