| public | jsonb_diff                                   | function | a jsonb, b jsonb                                                                                                                                                                                                                                                                                                                                              | jsonb                                                                                                                          | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.jsonb_diff(a jsonb, b jsonb)
 RETURNS jsonb
 LANGUAGE plpgsql
AS $function$
DECLARE
    key text;
    result jsonb := '{}'::jsonb;
BEGIN
    FOR key IN SELECT jsonb_object_keys(a || b) LOOP
        IF a->key IS DISTINCT FROM b->key THEN
            result := result || jsonb_build_object(key, jsonb_build_object('old', a->key, 'new', b->key));
        END IF;
    END LOOP;
    RETURN result;
END; $function$
