                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     |
| public | resequence_table                             | function | p_schema text, p_table text, p_pk_column text, p_sequence_name text                                                                                                                                                                                                                                                                                           | void                                                                                                                           | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.resequence_table(p_schema text, p_table text, p_pk_column text, p_sequence_name text DEFAULT NULL::text)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE
  v_full_table regclass;
  v_temp_offset bigint := 1000000000; -- large offset to avoid collisions
  v_empty boolean;
  v_map_table text := format('tmp_map_%s', p_table);
  v_child record;
  v_seq_exists boolean := false;
  v_seq_to_reset text := NULL;
  v_has_rows boolean;
  v_can_disable_triggers boolean := true;
  v_non_cascade_count int := 0;
  v_is_identity text;
  v_identity_generation text;
  v_toggled_identity boolean := false;
BEGIN
  v_full_table := (quote_ident(p_schema) || '.' || quote_ident(p_table))::regclass;

  -- Skip if table has no rows
  EXECUTE format('SELECT EXISTS (SELECT 1 FROM %I.%I)', p_schema, p_table) INTO v_has_rows;
  IF NOT v_has_rows THEN
    RAISE NOTICE 'Table %.% has no rows; skipping resequence', p_schema, p_table;
    RETURN;
  END IF;

  -- Create mapping temp table
  EXECUTE format('CREATE TEMP TABLE %I (old_id bigint PRIMARY KEY, new_id bigint UNIQUE, temp_id bigint UNIQUE)', v_map_table);

  EXECUTE format(
    'INSERT INTO %1$s (old_id, new_id, temp_id)
     SELECT %2$I, dense_rank() OVER (ORDER BY %2$I), dense_rank() OVER (ORDER BY %2$I) + %3$L
     FROM %4$I.%5$I',
    v_map_table, p_pk_column, v_temp_offset, p_schema, p_table
  );

  -- If PK column is an identity GENERATED ALWAYS, temporarily relax to BY DEFAULT so we can update values
  SELECT c.is_identity, c.identity_generation
    INTO v_is_identity, v_identity_generation
  FROM information_schema.columns c
  WHERE c.table_schema = p_schema
    AND c.table_name = p_table
    AND c.column_name = p_pk_column;

  IF v_is_identity = 'YES' AND upper(coalesce(v_identity_generation, '')) = 'ALWAYS' THEN
    EXECUTE format('ALTER TABLE %I.%I ALTER COLUMN %I SET GENERATED BY DEFAULT', p_schema, p_table, p_pk_column);
    v_toggled_identity := true;
    RAISE NOTICE 'Temporarily set %.%.% to GENERATED BY DEFAULT to allow updates', p_schema, p_table, p_pk_column;
  END IF;

  -- Try to disable triggers on parent table; if not permitted or table has pending trigger events, switch to CASCADE-only mode
  BEGIN
    EXECUTE format('ALTER TABLE %I.%I DISABLE TRIGGER ALL', p_schema, p_table);
  EXCEPTION
    WHEN insufficient_privilege THEN
      v_can_disable_triggers := false;
      RAISE NOTICE 'Insufficient privilege to DISABLE TRIGGER on %.%; will require ON UPDATE CASCADE on all referencing FKs', p_schema, p_table;
    WHEN object_not_in_prerequisite_state THEN -- SQLSTATE 55006: cannot alter due to pending trigger events
      v_can_disable_triggers := false;
      RAISE NOTICE 'Cannot DISABLE TRIGGER on %.% due to pending trigger events; will use ON UPDATE CASCADE path', p_schema, p_table;
  END;

  IF v_can_disable_triggers THEN
    -- Disable triggers on all referencing child tables
    FOR v_child IN (
      SELECT ns.nspname AS child_schema, c.relname AS child_table, a.attname AS fk_column
      FROM pg_constraint con
      JOIN pg_class c ON c.oid = con.conrelid
      JOIN pg_namespace ns ON ns.oid = c.relnamespace
      JOIN LATERAL unnest(con.conkey) WITH ORDINALITY u(attnum, ord) ON true
      JOIN pg_attribute a ON a.attrelid = con.conrelid AND a.attnum = u.attnum
      WHERE con.contype = 'f'
        AND con.confrelid = v_full_table
        AND ns.nspname = p_schema
        AND array_length(con.conkey, 1) = 1 -- single-column FK only
    ) LOOP
      EXECUTE format('ALTER TABLE %I.%I DISABLE TRIGGER ALL', v_child.child_schema, v_child.child_table);
    END LOOP;
  ELSE
    -- Validate that all referencing foreign keys have ON UPDATE CASCADE
    SELECT count(*) INTO v_non_cascade_count
    FROM pg_constraint con
    JOIN pg_class c ON c.oid = con.conrelid
    JOIN pg_namespace ns ON ns.oid = c.relnamespace
    WHERE con.contype = 'f'
      AND con.confrelid = v_full_table
      AND ns.nspname = p_schema
      AND array_length(con.conkey, 1) = 1
      AND con.confupdtype <> 'c'; -- not CASCADE

    IF v_non_cascade_count > 0 THEN
      RAISE EXCEPTION 'Cannot resequence %.% without superuser privileges unless all referencing FKs are ON UPDATE CASCADE. Found % non-cascade FKs. Run 29_enable_on_update_cascade.sql for this parent table and retry.', p_schema, p_table, v_non_cascade_count;
    END IF;
  END IF;

  -- Stage 1: update parent PKs to temp_id (children handled either by manual updates or cascades)
  EXECUTE format(
    'UPDATE %1$I.%2$I t
     SET %3$I = m.temp_id
     FROM %4$I m
     WHERE t.%3$I = m.old_id AND t.%3$I <> m.temp_id',
    p_schema, p_table, p_pk_column, v_map_table
  );

  IF v_can_disable_triggers THEN
    -- Stage 1: manual child FK updates to temp_id (not needed when CASCADE is enforced)
    FOR v_child IN (
      SELECT ns.nspname AS child_schema, c.relname AS child_table, a.attname AS fk_column
      FROM pg_constraint con
      JOIN pg_class c ON c.oid = con.conrelid
      JOIN pg_namespace ns ON ns.oid = c.relnamespace
      JOIN LATERAL unnest(con.conkey) WITH ORDINALITY u(attnum, ord) ON true
      JOIN pg_attribute a ON a.attrelid = con.conrelid AND a.attnum = u.attnum
      WHERE con.contype = 'f'
        AND con.confrelid = v_full_table
        AND ns.nspname = p_schema
        AND array_length(con.conkey, 1) = 1
    ) LOOP
      EXECUTE format(
        'UPDATE %1$I.%2$I c
         SET %3$I = m.temp_id
         FROM %4$I m
         WHERE c.%3$I = m.old_id AND c.%3$I <> m.temp_id',
        v_child.child_schema, v_child.child_table, v_child.fk_column, v_map_table
      );
    END LOOP;
  END IF;

  -- Stage 2: update parent PKs from temp_id to final new_id
  EXECUTE format(
    'UPDATE %1$I.%2$I t
     SET %3$I = m.new_id
     FROM %4$I m
     WHERE t.%3$I = m.temp_id AND m.new_id <> m.temp_id',
    p_schema, p_table, p_pk_column, v_map_table
  );

  IF v_can_disable_triggers THEN
    -- Stage 2: manual child FK updates to final new_id (not needed when CASCADE is enforced)
    FOR v_child IN (
      SELECT ns.nspname AS child_schema, c.relname AS child_table, a.attname AS fk_column
      FROM pg_constraint con
      JOIN pg_class c ON c.oid = con.conrelid
      JOIN pg_namespace ns ON ns.oid = c.relnamespace
      JOIN LATERAL unnest(con.conkey) WITH ORDINALITY u(attnum, ord) ON true
      JOIN pg_attribute a ON a.attrelid = con.conrelid AND a.attnum = u.attnum
      WHERE con.contype = 'f'
        AND con.confrelid = v_full_table
        AND ns.nspname = p_schema
        AND array_length(con.conkey, 1) = 1
    ) LOOP
      EXECUTE format(
        'UPDATE %1$I.%2$I c
         SET %3$I = m.new_id
         FROM %4$I m
         WHERE c.%3$I = m.temp_id AND m.new_id <> m.temp_id',
        v_child.child_schema, v_child.child_table, v_child.fk_column, v_map_table
      );
    END LOOP;
  END IF;

  IF v_can_disable_triggers THEN
    -- Re-enable triggers on all referencing child tables
    FOR v_child IN (
      SELECT DISTINCT ns.nspname AS child_schema, c.relname AS child_table
      FROM pg_constraint con
      JOIN pg_class c ON c.oid = con.conrelid
      JOIN pg_namespace ns ON ns.oid = c.relnamespace
      WHERE con.contype = 'f'
        AND con.confrelid = v_full_table
        AND ns.nspname = p_schema
    ) LOOP
      EXECUTE format('ALTER TABLE %I.%I ENABLE TRIGGER ALL', v_child.child_schema, v_child.child_table);
    END LOOP;
    -- Re-enable triggers on parent table
    EXECUTE format('ALTER TABLE %I.%I ENABLE TRIGGER ALL', p_schema, p_table);
  END IF;

  -- Reset sequence: prefer provided name, otherwise auto-detect serial/identity sequence
  v_seq_to_reset := p_sequence_name;
  IF v_seq_to_reset IS NULL THEN
    EXECUTE format('SELECT pg_get_serial_sequence(%L, %L)',
                   p_schema || '.' || p_table,
                   p_pk_column)
      INTO v_seq_to_reset;
  END IF;

  IF v_seq_to_reset IS NOT NULL THEN
    SELECT EXISTS (
      SELECT 1 FROM pg_class s
      JOIN pg_namespace n ON n.oid = s.relnamespace
      WHERE (n.nspname || '.' || s.relname) = v_seq_to_reset
         OR s.oid::regclass::text = v_seq_to_reset
    ) INTO v_seq_exists;

    IF v_seq_exists THEN
      EXECUTE format('SELECT setval(%L::regclass, COALESCE((SELECT MAX(%I) FROM %I.%I), 0))',
                     v_seq_to_reset, p_pk_column, p_schema, p_table);
    ELSE
      RAISE NOTICE 'Sequence % not found; skipped setval', v_seq_to_reset;
    END IF;
  ELSE
    RAISE NOTICE 'No sequence associated with %.%(%); skipped setval', p_schema, p_table, p_pk_column;
  END IF;

  -- Cleanup temp map
  EXECUTE format('DROP TABLE IF EXISTS %I', v_map_table);

  -- Restore identity to GENERATED ALWAYS if it was toggled
  IF v_toggled_identity THEN
    EXECUTE format('ALTER TABLE %I.%I ALTER COLUMN %I SET GENERATED ALWAYS', p_schema, p_table, p_pk_column);
    RAISE NOTICE 'Restored %.%.% to GENERATED ALWAYS', p_schema, p_table, p_pk_column;
  END IF;

  RAISE NOTICE 'Resequenced %.% (%), sequence: %', p_schema, p_table, p_pk_column, COALESCE(p_sequence_name, 'N/A');
END
