                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               |
| public | resolve_login_identifier                     | function | p_identifier text                                                                                                                                                                                                                                                                                                                                             | TABLE(user_id bigint, email text)                                                                                              | plpgsql  | s          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.resolve_login_identifier(p_identifier text)
 RETURNS TABLE(user_id bigint, email text)
 LANGUAGE plpgsql
 STABLE
AS $function$
DECLARE
    v_norm text := lower(trim(p_identifier));
BEGIN
    RETURN QUERY
    SELECT u.user_id, u.email
      FROM users u
     WHERE lower(u.email) = v_norm
        OR lower(u.username) = v_norm
        OR u.contact_number = p_identifier
     LIMIT 1;
END; $function$
