                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    |
| public | soft_delete_cascade_guardians                | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.soft_delete_cascade_guardians()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    UPDATE patients
    SET is_deleted = TRUE,
        deleted_at = COALESCE(NEW.deleted_at, NOW()),
        deleted_by = NEW.deleted_by,
        updated_at = now(),
        updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE guardian_id = NEW.guardian_id;
    RETURN NEW;
END;
