| public | soft_restore_cascade_guardians               | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.soft_restore_cascade_guardians()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    UPDATE patients
    SET is_deleted = FALSE,
        deleted_at = NULL,
        deleted_by = NULL,
        updated_at = now(),
        updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE guardian_id = NEW.guardian_id;
    RETURN NEW;
END;
