| public | soft_restore_cascade_inventory               | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.soft_restore_cascade_inventory()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    UPDATE inventorytransactions
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE inventory_id = NEW.inventory_id;

    UPDATE immunizations
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE inventory_id = NEW.inventory_id;

    UPDATE deworming
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE inventory_id = NEW.inventory_id;

    UPDATE vitamina
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE inventory_id = NEW.inventory_id;

    -- Receiving Report Items tied to this inventory
    UPDATE receiving_report_items
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE inventory_id = NEW.inventory_id;

    RETURN NEW;
END;
