| public | soft_restore_cascade_patients                | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.soft_restore_cascade_patients()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    UPDATE visits
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE patient_id = NEW.patient_id;

    UPDATE patientschedule
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE patient_id = NEW.patient_id;

    UPDATE birthhistory
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
