| public | soft_restore_cascade_vaccines                | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.soft_restore_cascade_vaccines()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    UPDATE schedule_master
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    UPDATE schedule_doses sd
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE sd.schedule_id IN (SELECT id FROM schedule_master WHERE vaccine_id = NEW.vaccine_id);

    UPDATE patientschedule
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    UPDATE inventory
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    UPDATE immunizations
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    UPDATE deworming
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    UPDATE vitamina
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    -- Receiving Report Items tied to this vaccine
    UPDATE receiving_report_items
    SET is_deleted = FALSE, deleted_at = NULL, deleted_by = NULL,
        updated_at = now(), updated_by = NULLIF(current_setting('app.user_id', true),'')::bigint
    WHERE vaccine_id = NEW.vaccine_id;

    RETURN NEW;
END;
