                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            |
| public | trg_inventory_requests_audit                 | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.trg_inventory_requests_audit()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    IF TG_OP='INSERT' THEN
        NEW.created_at := now();
        IF NEW.created_by IS NULL THEN NEW.created_by := NULLIF(current_setting('app.user_id', true),'')::bigint; END IF;
    ELSE
        NEW.updated_at := now();
        NEW.updated_by := NULLIF(current_setting('app.user_id', true),'')::bigint;
    END IF;
    RETURN NEW;
END; $function$
