                                                                                                                                                                                                                                                                                |
| public | update_patient_age_fields                    | function |                                                                                                                                                                                                                                                                                                                                                               | trigger                                                                                                                        | plpgsql  | v          | false            | false     | u        | null                                                                                                                           | CREATE OR REPLACE FUNCTION public.update_patient_age_fields()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
DECLARE
    months INTEGER;
    days INTEGER;
BEGIN
    months := EXTRACT(YEAR FROM age(NEW.date_of_birth)) * 12 + EXTRACT(MONTH FROM age(NEW.date_of_birth));
    days := EXTRACT(DAY FROM age(NEW.date_of_birth));
    NEW.age_months := months;
    NEW.age_days := days;
    RETURN NEW;
END;
$function$
