// sw.js - Service Worker for Guardian Offline Mode and Push Notifications
import { precacheAndRoute } from 'workbox-precaching';

// Precache all assets generated by Vite
precacheAndRoute(self.__WB_MANIFEST);

const CACHE_NAME = 'guardian-cache-v1';

self.addEventListener('install', (event) => {
  console.log('Service Worker installing.');
  event.waitUntil(
    caches.open(CACHE_NAME).then(cache => {
      return cache.addAll([
        '/',
        '/index.html',
        '/static/js/app.js',
        '/static/css/app.css'
        // Add more static assets as needed
      ]);
    })
  );
});

self.addEventListener('activate', (event) => {
  console.log('Service Worker activating.');
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheName !== CACHE_NAME) {
            console.log('Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
});

self.addEventListener('fetch', (event) => {
  // For now, just network-first for dynamic content, cache-first for static
  if (event.request.url.includes('/api/')) {
    event.respondWith(
      fetch(event.request).catch(() => {
        // If offline, could return cached response, but for now, let it fail
        return new Response(JSON.stringify({ error: 'Offline' }), {
          status: 503,
          headers: { 'Content-Type': 'application/json' }
        });
      })
    );
  } else {
    event.respondWith(
      caches.match(event.request).then(response => {
        return response || fetch(event.request);
      })
    );
  }
});

// Handle messages from the main thread (for testing)
self.addEventListener('message', (event) => {
  console.log('Service Worker received message:', event.data);
  
  if (event.data && event.data.type === 'TEST_PUSH') {
    const { title, body, icon, badge, data } = event.data.data;
    
    const options = {
      body: body,
      icon: icon || '/icons/icon-192x192.png',
      badge: badge || '/icons/icon-96x96.png',
      tag: 'test-notification',
      requireInteraction: false,
      data: data || {},
      timestamp: Date.now(),
      vibrate: [200, 100, 200]
    };

    console.log('Showing test notification:', title, options);
    
    self.registration.showNotification(title, options)
      .then(() => {
        console.log('âœ… Test notification shown successfully');
      })
      .catch((error) => {
        console.error('âŒ Error showing test notification:', error);
      });
  }
});

// Handle push notifications
self.addEventListener('push', (event) => {
  console.log('ðŸ”” Push event received');
  console.log('Push event data:', event.data);

  let data = {
    title: 'ImmunizeMe',
    body: 'You have a new notification',
    icon: '/icons/icon-192x192.png',
    badge: '/icons/icon-96x96.png',
    tag: 'default-notification',
    data: {
      url: '/notifications'
    }
  };

  // Parse notification data
  if (event.data) {
    try {
      const parsedData = event.data.json();
      console.log('ðŸ“¦ Parsed push data:', parsedData);
      data = parsedData;
    } catch (e) {
      console.warn('âš ï¸ Could not parse push data as JSON, using text:', e);
      data.body = event.data.text();
    }
  } else {
    console.warn('âš ï¸ Push event has no data');
  }

  const options = {
    body: data.body,
    icon: data.icon || '/icons/icon-192x192.png',
    badge: data.badge || '/icons/icon-96x96.png',
    tag: data.tag || 'notification',
    requireInteraction: data.requireInteraction || false,
    data: data.data || {},
    actions: data.actions || [],
    timestamp: data.timestamp || Date.now(),
    vibrate: [200, 100, 200]
  };

  console.log('ðŸ“¢ Attempting to show notification:', data.title, options);

  event.waitUntil(
    self.registration.showNotification(data.title, options)
      .then(() => {
        console.log('âœ… Notification shown successfully');
      })
      .catch((error) => {
        console.error('âŒ Error showing notification:', error);
      })
  );
});

// Handle notification click
self.addEventListener('notificationclick', (event) => {
  console.log('Notification clicked:', event);

  event.notification.close();

  // Handle action buttons
  if (event.action) {
    console.log('Action clicked:', event.action);
    if (event.action === 'dismiss') {
      return;
    }
  }

  // Get the URL to open
  const urlToOpen = event.notification.data?.url || '/notifications';

  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true })
      .then((clientList) => {
        // Check if there's already a window open
        for (const client of clientList) {
          if (client.url.includes(self.location.origin) && 'focus' in client) {
            client.focus();
            client.navigate(urlToOpen);
            return;
          }
        }
        // No window open, open a new one
        if (clients.openWindow) {
          return clients.openWindow(urlToOpen);
        }
      })
  );
});

// Background sync for queued messages
self.addEventListener('sync', (event) => {
  if (event.tag === 'send-messages') {
    event.waitUntil(syncQueuedMessages());
  } else if (event.tag === 'sync-profile-edits') {
    event.waitUntil(syncProfileEdits());
  }
});

async function syncQueuedMessages() {
  console.log('Syncing queued messages...');
  try {
    // Note: IndexedDB operations removed from service worker
    // Sync logic should be handled by the main app thread
    console.log('Messages sync delegated to main thread');
  } catch (error) {
    console.error('Failed to sync messages:', error);
  }
}

async function syncProfileEdits() {
  console.log('Syncing profile edits...');
  try {
    // Note: IndexedDB operations removed from service worker
    // Sync logic should be handled by the main app thread
    console.log('Profile edits sync delegated to main thread');
  } catch (error) {
    console.error('Failed to sync profile edits:', error);
  }
}
