-- Table and column comments
WITH rels AS (
  SELECT n.nspname AS schema, c.relname AS table_name, c.oid
  FROM pg_class c
  JOIN pg_namespace n ON n.oid = c.relnamespace
  WHERE c.relkind IN ('r','p','v','m','f')
    AND n.nspname NOT IN ('pg_catalog','information_schema')
), t_comments AS (
  SELECT r.schema, r.table_name, obj_description(r.oid, 'pg_class') AS table_comment
  FROM rels r
), c_comments AS (
  SELECT r.schema, r.table_name, a.attname AS column_name, col_description(r.oid, a.attnum) AS column_comment
  FROM rels r
  JOIN pg_attribute a ON a.attrelid = r.oid AND a.attnum > 0 AND NOT a.attisdropped
)
SELECT 'table' AS kind, schema, table_name, NULL::text AS column_name, table_comment AS comment
FROM t_comments
UNION ALL
SELECT 'column', schema, table_name, column_name, column_comment
FROM c_comments
ORDER BY kind, schema, table_name, column_name NULLS FIRST;

